import { motion } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';

const Section = styled.section`
  background: 
    linear-gradient(125deg, #4B2094 0%, #2B1055 35%, #1A0B2E 50%, #2B1055 65%, #4B2094 100%),
