    radial-gradient(circle at 0% 50%, rgba(255, 255, 255, 0.2) 0%, transparent 50%),
    radial-gradient(circle at 100% 50%, rgba(255, 255, 255, 0.2) 0%, transparent 50%);
  padding: 6rem 2rem;
  color: white;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: 
      radial-gradient(circle at 50% 50%, rgba(255, 255, 255, 0.25) 0%, transparent 40%),
      radial-gradient(circle at 30% 50%, rgba(255, 255, 255, 0.2) 0%, transparent 50%),
      radial-gradient(circle at 70% 50%, rgba(255, 255, 255, 0.2) 0%, transparent 50%);
    animation: rotate 30s linear infinite;
    pointer-events: none;
    opacity: 0.5;
    mix-blend-mode: soft-light;
  }

  &::after {
    content: '';
    position: absolute;
    inset: 0;
    background: 
      linear-gradient(90deg, 
        rgba(255, 255, 255, 0.15) 0%, 
        transparent 20%, 
        rgba(255, 255, 255, 0.1) 50%, 
        transparent 80%, 
        rgba(255, 255, 255, 0.15) 100%
      ),
      linear-gradient(180deg, 
        rgba(255, 255, 255, 0.2) 0%, 
        transparent 30%, 
        rgba(255, 255, 255, 0.1) 50%, 
        transparent 70%, 
        rgba(255, 255, 255, 0.2) 100%
      );
    pointer-events: none;
    mix-blend-mode: overlay;
  }

  @keyframes rotate {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
`;

const GlowBackground = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background: 
    radial-gradient(circle at 20% 20%, rgba(255, 255, 255, 0.3) 0%, transparent 60%),
    radial-gradient(circle at 80% 80%, rgba(255, 255, 255, 0.3) 0%, transparent 60%),
    radial-gradient(circle at 50% 50%, rgba(75, 32, 148, 0.5) 0%, transparent 70%);
  pointer-events: none;
  opacity: 0.6;
  mix-blend-mode: screen;
  filter: blur(20px);
`;

const ShineOverlay = styled.div`
  position: absolute;
  inset: 0;
  background: 
    linear-gradient(45deg, 
      transparent 0%, 
      rgba(255, 255, 255, 0.1) 30%, 
      rgba(255, 255, 255, 0.15) 50%, 
      rgba(255, 255, 255, 0.1) 70%, 
      transparent 100%
    );
  pointer-events: none;
  mix-blend-mode: overlay;
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  position: relative;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 6rem;
`;

const Title = styled.h2`
  font-size: 3rem;
  color: white;
  margin-bottom: 1rem;
`;

const Subtitle = styled.h3`
  font-size: 1.5rem;
  color: rgba(255, 255, 255, 0.9);
  margin-bottom: 2rem;
`;

const Description = styled.p`
  color: rgba(255, 255, 255, 0.9);
  max-width: 800px;
  margin: 0 auto;
  line-height: 1.6;
`;

const PricingGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 2rem;
  margin-top: 4rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
  }
`;

const PriceCard = styled(motion.div)`
  background: linear-gradient(
    145deg,
    rgba(255, 255, 255, 0.1) 0%,
    rgba(255, 255, 255, 0.05) 45%,
    rgba(255, 255, 255, 0.02) 100%
  );
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 24px;
  padding: 2.5rem;
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  height: 100%;
  box-shadow: 
    0 8px 32px rgba(0, 0, 0, 0.3),
    0 2px 4px rgba(255, 255, 255, 0.05),
    inset 0 2px 4px rgba(255, 255, 255, 0.05);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 200%;
    height: 100%;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.05),
      transparent
    );
    transition: 0.5s;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 
      0 12px 40px rgba(0, 0, 0, 0.4),
      0 4px 8px rgba(255, 255, 255, 0.1),
      inset 0 2px 4px rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.25);
    background: linear-gradient(
      145deg,
      rgba(255, 255, 255, 0.15) 0%,
      rgba(255, 255, 255, 0.07) 45%,
      rgba(255, 255, 255, 0.03) 100%
    );
  }

  &::after {
    content: '';
    position: absolute;
    inset: 1px;
    background: rgba(0, 0, 0, 0.4);
    border-radius: 24px;
    z-index: -1;
  }
`;

const PlanName = styled.h4`
  font-size: 1.75rem;
  color: white;
  margin-bottom: 0.5rem;
`;

const PlanPrice = styled.div`
  font-size: 2.5rem;
  color: white;
  margin: 1.5rem 0;
  font-weight: bold;

  span {
    font-size: 1rem;
    opacity: 0.7;
  }
`;

const PlanDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 2rem;
  font-size: 1.1rem;
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 2rem 0;
  flex: 1;
`;

const Feature = styled.li`
  color: rgba(255, 255, 255, 0.9);
  margin-bottom: 1rem;
  padding-left: 1.5rem;
  position: relative;

  &::before {
    content: "âœ“";
    color: white;
    position: absolute;
    left: 0;
  }
`;

const CTAButton = styled(motion.button)`
  width: 100%;
  padding: 1rem;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(255, 255, 255, 0.85) 100%);
  border: none;
  border-radius: 12px;
  color: #2B1055;
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);

  &:hover {
    background: white;
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(255, 255, 255, 0.15);
  }
`;

const BestValue = styled.div`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0.05) 100%);
  padding: 0.5rem 1rem;
  border-radius: 20px;
  font-size: 0.9rem;
  color: white;
  backdrop-filter: blur(5px);
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const ButtonWrapper = styled.div`
  margin-top: auto;
  width: 100%;
`;

const plans = [
  {
    name: "Basic Plan",
    price: "15",
    description: "Ideal for Innovators and Startups",
    features: [
      "Access to a shared GPU server",
      "500 GB of cloud storage",
      "Standard support (9 AM - 6 PM)"
    ],
    bestFor: "Small projects, prototypes, and individual developers"
  },
  {
