import styled from 'styled-components';
import { motion } from 'framer-motion';

const FooterContainer = styled.footer`
  background: ${({ theme }) => theme.gradients.purple};
  color: white;
  padding: 4rem 2rem;
  position: relative;
  overflow: hidden;
`;

const NewsletterSection = styled.div`
  max-width: 1400px;
  margin: 0 auto 4rem;
  text-align: center;
`;

const NewsletterTitle = styled.h3`
  font-size: 2rem;
  margin-bottom: 0.5rem;
  color: white;
`;

const NewsletterSubtitle = styled.p`
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 2rem;
  font-size: 1rem;
`;

const SubscribeForm = styled.form`
  display: flex;
  max-width: 500px;
  margin: 0 auto;
  gap: 0.5rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    flex-direction: column;
  }
`;

const EmailInput = styled.input`
  flex: 1;
  padding: 0.8rem 1.5rem;
  border: none;
  border-radius: 25px;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);

  &::placeholder {
    color: rgba(255, 255, 255, 0.6);
  }

  &:focus {
    outline: none;
    border-color: rgba(255, 255, 255, 0.5);
  }
`;

const SubscribeButton = styled.button`
  padding: 0.8rem 2rem;
  border: none;
  border-radius: 25px;
  background: #e31837;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background: #ff1a1a;
  }
`;

const SocialLinks = styled.div`
  display: flex;
  justify-content: center;
  gap: 1.5rem;
  margin-top: 2rem;

  a {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    transition: color 0.3s ease;

    &:hover {
      color: white;
    }
  }
`;

const Copyright = styled.div`
  text-align: center;
  margin-top: 3rem;
  padding-top: 2rem;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  color: rgba(255, 255, 255, 0.8);
  font-size: 0.9rem;
`;

const Footer = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle newsletter subscription
  };

  return (
    <FooterContainer>
      <NewsletterSection>
        <NewsletterTitle>Let's connect!</NewsletterTitle>
        <NewsletterSubtitle>
          Subscribe & be the first to know about the latest product updates & announcements.
        </NewsletterSubtitle>
        <SubscribeForm onSubmit={handleSubmit}>
          <EmailInput 
            type="email" 
            placeholder="Type your e-mail here..."
            required
          />
          <SubscribeButton type="submit">Join</SubscribeButton>
        </SubscribeForm>
        <SocialLinks>
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-facebook-f"></i>
          </a>
          <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-twitter"></i>
          </a>
          <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-linkedin-in"></i>
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-instagram"></i>
          </a>
          <a href="https://youtube.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-youtube"></i>
          </a>
        </SocialLinks>
      </NewsletterSection>

      <Copyright>
        <p>&copy; {new Date().getFullYear()} CypherBox. All rights reserved.</p>
      </Copyright>
    </FooterContainer>
  );
};

export default Footer;
