import styled from 'styled-components';
import { motion, useInView } from 'framer-motion';
import { useRef } from 'react';

const Section = styled.section`
  background-color: ${({ theme }) => theme.colors.black};
  padding: 6rem 2rem;
  color: white;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 6rem;
`;

const Title = styled.h2`
  font-size: 3rem;
  color: #00FFFF;
  margin-bottom: 1rem;
`;

const Subtitle = styled.h3`
  font-size: 1.5rem;
  color: rgba(0, 255, 255, 0.9);
  margin-bottom: 2rem;
`;

const Description = styled.p`
  color: rgba(255, 255, 255, 0.9);
  max-width: 800px;
  margin: 0 auto;
  line-height: 1.6;
`;

const TechGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  perspective: 1000px;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
  }
`;

const TechCard = styled(motion.div)`
  background: linear-gradient(
    145deg,
    rgba(0, 255, 255, 0.1) 0%,
    rgba(0, 255, 255, 0.05) 100%
  );
  border: 1px solid rgba(0, 255, 255, 0.2);
  border-radius: 20px;
  padding: 2rem;
  position: relative;
  transform-style: preserve-3d;
  cursor: pointer;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 20px;
    padding: 1px;
    background: linear-gradient(
      145deg,
      rgba(0, 255, 255, 0.4) 0%,
      transparent 100%
    );
    -webkit-mask: linear-gradient(#fff 0 0) content-box,
                 linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
  }

  &::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 20px;
    background: radial-gradient(
      800px circle at var(--mouse-x) var(--mouse-y),
      rgba(0, 255, 255, 0.1),
      transparent 40%
    );
    opacity: 0;
    transition: opacity 0.3s;
  }

  &:hover::after {
    opacity: 1;
  }
`;

const CardTitle = styled.h4`
  font-size: 1.5rem;
  color: #00FFFF;
  margin-bottom: 1rem;
  transform: translateZ(20px);
`;

const CardSubtitle = styled.h5`
  font-size: 1.1rem;
  color: rgba(0, 255, 255, 0.9);
  margin-bottom: 1.5rem;
  transform: translateZ(15px);
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 1.5rem 0;
  transform: translateZ(10px);
`;

const Feature = styled.li`
  color: rgba(255, 255, 255, 0.9);
  margin-bottom: 0.75rem;
  padding-left: 1.25rem;
  position: relative;

  &::before {
    content: "→";
    color: #00FFFF;
    position: absolute;
    left: 0;
  }
`;

const UseCase = styled.div`
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid rgba(0, 255, 255, 0.1);
  transform: translateZ(5px);
`;

const UseCaseTitle = styled.h6`
  color: #00FFFF;
  font-size: 1rem;
  margin-bottom: 0.5rem;
`;

const Technology = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });

  const handleMouseMove = (e, card) => {
    const rect = card.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    card.style.setProperty('--mouse-x', `${x}px`);
    card.style.setProperty('--mouse-y', `${y}px`);
  };

  const technologies = [
    // ... your technology items here (same structure as in the content)
  ];

  return (
    <Section>
      <Container>
        <Header>
          <Title>Our Technology</Title>
          <Subtitle>The Backbone of Innovation</Subtitle>
          <Description>
            At CypherBox, we leverage cutting-edge technology to provide robust, scalable, 
            and high-performance infrastructure. Our state-of-the-art data centers are optimized 
            for AI, machine learning, and cloud computing, enabling you to unlock the full 
            potential of your projects.
          </Description>
        </Header>

        <TechGrid
          ref={ref}
          as={motion.div}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
          variants={{
            visible: {
              transition: {
                staggerChildren: 0.1
              }
            }
          }}
          onMouseMove={(e) => {
            const cards = e.currentTarget.getElementsByClassName('tech-card');
            for (const card of cards) {
              handleMouseMove(e, card);
            }
          }}
        >
          {technologies.map((tech, index) => (
            <TechCard
              key={index}
              className="tech-card"
              variants={{
                hidden: { opacity: 0, y: 20, rotateX: -10 },
                visible: {
                  opacity: 1,
                  y: 0,
                  rotateX: 0,
                  transition: {
                    type: "spring",
                    damping: 20,
                    stiffness: 100
                  }
                }
              }}
            >
              <CardTitle>{tech.title}</CardTitle>
              <CardSubtitle>{tech.subtitle}</CardSubtitle>
              <p>{tech.description}</p>
              <FeatureList>
                {tech.features.map((feature, featureIndex) => (
                  <Feature key={featureIndex}>{feature}</Feature>
                ))}
              </FeatureList>
              <UseCase>
                <UseCaseTitle>Example Use Case:</UseCaseTitle>
                <p>{tech.useCase}</p>
              </UseCase>
            </TechCard>
          ))}
        </TechGrid>
      </Container>
    </Section>
  );
};

export default Technology;
