import styled from 'styled-components';
import { motion } from 'framer-motion';
import SplineAnimation from './SplineAnimation';

const HeroSection = styled.header`
  min-height: 85vh;
  display: flex;
  align-items: center;
  padding: 2rem;
  position: relative;
  overflow: hidden;
  clip-path: inset(0 0 10% 0);
  background-color: ${({ theme }) => theme.colors.black};
`;

const AnimationBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;

  iframe {
    width: 100%;
    height: 100%;
    border: none;
    transform: scale(1.2);
  }
`;

const HeroContainer = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  width: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  gap: 0;
  padding: 0 2rem;
  position: relative;
  z-index: 2;
  margin-bottom: 15%;
  padding-top: 50px;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    flex-direction: column-reverse;
    text-align: center;
    padding: 0 1rem;
    justify-content: center;
  }
`;

const ContentContainer = styled(motion.div)`
  flex: 1;
  padding: 2rem;
  max-width: 800px;
  margin-left: -100px;
  margin-top: 100px;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    margin-left: 0;
    margin-top: 50px;
    text-align: center;
  }
`;

const Headline = styled(motion.h1)`
  font-size: clamp(2.5rem, 3vw, 3rem);
  font-weight: bold;
  margin-bottom: 0.5rem;
  color: white;
  line-height: 1.2;
  text-align: left;
  max-width: 750px;
  display: flex;
  flex-direction: column;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    text-align: center;
    font-size: clamp(2rem, 2.5vw, 2.5rem);
  }
`;

const HeadlineSpan = styled.span`
  display: block;
  white-space: nowrap;
`;

const Subheadline = styled(motion.p)`
  font-size: 1.25rem;
  color: white;
  margin-bottom: 2.5rem;
  line-height: 1.6;
  max-width: 600px;
`;

const CTAButton = styled(motion.button)`
  padding: 1rem 2rem;
  background: ${({ theme }) => theme.gradients.purple};
  color: white;
  border: none;
  border-radius: 5px;
  font-size: 1.1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${({ theme }) => theme.gradients.purpleHover};
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(111, 66, 193, 0.2);
  }
`;

const Hero = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut",
      },
    },
  };

  return (
    <HeroSection>
      <AnimationBackground>
        <iframe 
          src='https://my.spline.design/interfaceii-df2d3333797f27b05e95be49d9150480/' 
          frameBorder='0'
          title="Interface Animation"
        />
      </AnimationBackground>
      <HeroContainer>
        <ContentContainer
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <Headline variants={itemVariants}>
            <HeadlineSpan>Powering the Future of</HeadlineSpan>
            <HeadlineSpan>AI and Cloud Computing</HeadlineSpan>
          </Headline>
          <Subheadline variants={itemVariants}>
            High-performance, scalable, and reliable data centers tailored for innovators, 
            enterprises, and educational institutions.
          </Subheadline>
          <CTAButton variants={itemVariants}>
            Explore More
          </CTAButton>
        </ContentContainer>
      </HeroContainer>
    </HeroSection>
  );
};

export default Hero;
