import { Suspense, useState, useEffect } from 'react';
import styled from 'styled-components';

const LoadingContainer = styled.div`
  width: 100%;
  height: 90%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: rgba(111, 66, 193, 0.1);
  border-radius: 8px;
  margin-top: -10%;
`;

const AnimationFrame = styled.iframe`
  width: 90%;
  height: 100%;
  border: none;
  position: absolute;
  top: 40%;
  left: 50%;
  transform: translate(-50%, -50%);
  clip-path: inset(0 0 10% 0);
  opacity: ${({ isLoaded }) => (isLoaded ? '1' : '0')};
  transition: opacity 0.3s ease;
`;

const LoadingSpinner = styled.div`
  border: 3px solid rgba(111, 66, 193, 0.1);
  border-top: 3px solid ${({ theme }) => theme.colors.purple};
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 1s linear infinite;
  margin-bottom: 1rem;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const SplineAnimation = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [shouldLoad, setShouldLoad] = useState(false);

  useEffect(() => {
    // Delay loading the iframe until after initial page render
    const timer = setTimeout(() => {
      setShouldLoad(true);
    }, 100);

    return () => clearTimeout(timer);
  }, []);

  // Preconnect to Spline domain
  useEffect(() => {
    const link = document.createElement('link');
    link.rel = 'preconnect';
    link.href = 'https://my.spline.design';
    document.head.appendChild(link);

    return () => {
      document.head.removeChild(link);
    };
  }, []);

  const handleLoad = () => {
    setIsLoaded(true);
  };

  return (
    <>
      {!isLoaded && (
        <LoadingContainer>
          <LoadingSpinner />
          <div>Loading 3D Animation...</div>
        </LoadingContainer>
      )}
      {shouldLoad && (
        <AnimationFrame 
          src='https://my.spline.design/database-ea358fb31c2823da4f9c6b65d1e92f13/'
          frameBorder='0'
          title="Database Animation"
          loading="lazy"
          onLoad={handleLoad}
          isLoaded={isLoaded}
        />
      )}
    </>
  );
};

export default SplineAnimation;
