import Hero from '../components/Hero';
import ServiceCard from '../components/ServiceCard';
import styled from 'styled-components';

const ServicesSection = styled.section`
  padding: 4rem 2rem;
`;

const SectionTitle = styled.h2`
  text-align: center;
  color: ${({ theme }) => theme.colors.yellow};
  margin-bottom: 3rem;
`;

const ServiceGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const Home = () => {
  const services = [
    {
      title: 'Cloud Hosting',
      description: 'Enterprise-grade cloud solutions with maximum security'
    },
    {
      title: 'Colocation',
      description: 'Secure facility for your hardware infrastructure'
    },
    {
      title: 'Backup & Recovery',
      description: 'Reliable backup solutions with instant recovery'
    }
  ];

  return (
    <>
      <Hero />
      <ServicesSection id="services">
        <SectionTitle>Our Services</SectionTitle>
        <ServiceGrid>
          {services.map((service, index) => (
            <ServiceCard 
              key={index}
              title={service.title}
              description={service.description}
            />
          ))}
        </ServiceGrid>
      </ServicesSection>
    </>
  );
};

export default Home;
